Select s.slackHandle, s.id, s.cohortId, s.firstName, s.lastName, c.Name, e.Name as ExerciseName, e.Language as ExerciseLanguage, e.Id as ExerciseId from Student s left join Cohort c on c.id = s.cohortId left join StudentExercise se on se.StudentId = s.Id left join Exercise e on se.ExerciseId = e.Id and se.StudentId = s.Id
